const Contact = require('./Contact') 
const data = require('./data.json');
let fs;
try {
  fs = require('fs');
} catch (e) {
  // fs is not available in the browser environment
}

class AddressBook {
    constructor(){
        this.contacts = data.map(
            ({ first_name, last_name, phone, email }) =>
              new Contact(first_name, last_name, phone, email)
          );
    }

    view_all() {
        return this.contacts
    }

    save_to_file() {
        const json = JSON.stringify(this.contacts);
        fs.writeFile('data.json', json, 'utf8', callback);
        return json;
    }
}

module.exports = AddressBook;
