import React, { useState, useEffect } from 'react';

const Home = ({ addressBook }) =>  {

    const [query, set_query] = useState('');

    const [query_json, set_query_json] = useState({});

    const handle_submit = (e) => {
        e.preventDefault();

        const query_json = {
            query: query
        };

        set_query_json(query_json);

        addressBook.search_contacts(query_json);

    };

    const [contacts, set_contacts] = useState([]);

    useEffect(() => {
        set_contacts(addressBook.search_contacts(query));
    }, [addressBook]);

    const handle_delete_contact = (index) => {
        addressBook.delete_contact(index);
        set_contacts(addressBook.search_contacts(query));
    };

    const temp = (index) => {
        console.log(index);
    };

    return (
        <div className="container pt-3">

            <form onSubmit={handle_submit}>

                <div className="mt-3">
                    <label htmlFor="search">Search</label>
                    <input
                        type="text"
                        className="form-control"
                        id="search"
                        placeholder="Search"
                        value={query}
                        onChange={(e) => set_query(e.target.value)}
                        required
                    />
                </div>
                <div className="mt-3">
                    <button type="submit" className="btn btn-danger">
                        Submit
                    </button>
                </div>
            </form>

            {contacts.length > 0 ? (
                <table className="table">
                    <thead className="bg-danger text-white">
                        <tr>
                            <th>First Name</th>
                            <th>Last Name</th>
                            <th>Phone</th>
                            <th>Email</th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        { contacts.map((contact, index) => (
                            <tr key = {index}>
                                <td>{contact.first_name}</td>
                                <td>{contact.last_name}</td>
                                <td>{contact.phone}</td>
                                <td>{contact.email}</td>
                                <td>
                                    <button className="btn btn-danger" onClick={() => temp(index)}>{"edit"}</button>
                                </td>
                                <td>
                                    <button className="btn btn-danger" onClick={() => handle_delete_contact(index)}>{"delete"}</button>
                                </td>
                            </tr>    
                        )) }
                    </tbody>
                </table>
            ) : (
                <p>No contacts found.</p>
            )}
        </div>
    );
};

export default Home;
